mapper.insert:
  		INSERT INTO PROJECT(
  			  PRJ_NO
  			, USER_NO
  			, PRJ_NAME
  			, DEPART_NO
  			, OPEN_YN
  			, ACTIVATE_YN
  			, PERIOD
  			, CREATE_DATE
  			, START_DATE
  			, END_DATE
  		)VALUES(
  			SEQ_PROJECT.NEXTVAL
  			, #{userNo}
  			, #{prjName}
  			, #{departNo}
  			, #{openYn}
  			, #{activateYn}
  			, #{period}
  			, CURRENT_DATE
  			, TO_DATE(#{startDate}, 'YYYY-MM-DD')
	  		, TO_DATE(#{endDate}, 'YYYY-MM-DD')
  		)

mapper.select:
SELECT COUNT(*) FROM PROJECT
WHERE DEPART_NO = #{departNo}
AND DELETE_YN = 'N'

SELECT USER_NO
FROM IAG_USER
WHERE NAME = #{name}

