mapper.insert:
INSERT INTO IAG_USER ( USER_NO, PWD, NAME, RESIDENT_NO, PHONE, EMAIL, ADDRESS, ENROLL_DATE, ACTIVITY_YN, PROFILE, PERSONNEL_RIGHT, PAYMENT_RIGHT, LEAVE_RIGHT, RESERVATIONS_RIGHT, BOARD_RIGHT, ATTENDENCE_RIGHT, SALARY_RIGHT
,POSITION_NO
,DEPARTMENT_NO
,JOB_NO
) VALUES ( #{userNo}, #{pwd}, #{name}, #{residentNo}, #{phone}, #{email}, #{address}, #{enrollDate}, #{activityYn}, #{profile}, #{personnelRight}, #{paymentRight}, #{leaveRight}, #{reservationsRight}, #{boardRight}, #{attendenceRight}, #{salaryRight}
, #{positionNo}
, #{departmentNo}
, #{jobNo}
)

INSERT INTO 
DEPARTMENT
(
	DEPARTMENT_NO,
	DEPARTMENT_NAME

		, TOP_DEPARTMENT


)
VALUES
(
	SEQ_DEPARTMENT.NEXTVAL,
	#{departmentName}

		, #{topDepartment}

)

mapper.select:
SELECT COUNT(USER_NO)
FROM IAG_USER
WHERE USER_NO = #{userNo}

SELECT * FROM( SELECT ROWNUM RN, A.* FROM ( SELECT * FROM IAG_USER U LEFT OUTER JOIN POSITION P ON (U.POSITION_NO = P.POSITION_NO) LEFT OUTER JOIN DEPARTMENT D ON (D.DEPARTMENT_NO = U.DEPARTMENT_NO) LEFT OUTER JOIN JOB J ON (J.JOB_NO = U.JOB_NO)
WHERE (U.USER_NO LIKE '%'|| #{search}||'%' OR TRIM(REPLACE(U.PHONE,'-','')) LIKE '%'||#{search}||'%' OR U.NAME LIKE '%'||#{search}||'%'
ORDER BY U.ENROLL_DATE ) A ) WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE IAG_USER SET
NAME = #{name},
RESIDENT_NO = #{residentNo},
PHONE = #{phone},
EMAIL = #{email},
ADDRESS = #{address},
ENROLL_DATE = #{enrollDate},
ACTIVITY_YN = #{activityYn},
PERSONNEL_RIGHT = #{personnelRight},
PAYMENT_RIGHT = #{paymentRight},
LEAVE_RIGHT = #{leaveRight},
RESERVATIONS_RIGHT = #{reservationsRight},
BOARD_RIGHT = #{boardRight},
ATTENDENCE_RIGHT = #{attendenceRight},
SALARY_RIGHT = #{salaryRight}

	,PROFILE = #{profile}


	,END_DATE = #{endDate}


	,PWD = #{pwd}


	,POSITION_NO = #{positionNo}


	,DEPARTMENT_NO = #{departmentNo}


	,JOB_NO = #{jobNo}

WHERE USER_NO = #{userNo}

UPDATE DEPARTMENT 
SET 
DEPARTMENT_NAME = #{departmentName} 
WHERE 
DEPARTMENT_NO = #{departmentNo}

UPDATE DEPARTMENT 
SET 

	TOP_DEPARTMENT = #{topDepartment}


	TOP_DEPARTMENT = NULL

WHERE 
DEPARTMENT_NO = #{departmentNo}

UPDATE POSITION SET 
POSITION_NAME = #{positionName}
WHERE POSITION_NO = #{positionNo}

UPDATE JOB SET 
JOB_NAME = #{jobName}
WHERE JOB_NO = #{jobNo}
