mapper.update:
  			UPDATE MEETING_ROOM
  			SET
  				      ROOM_NO = #{roomNo}

  					, ROOM_NAME = #{roomName}


  					, DETAIL = #{detail}


	  				, ACTIVATE_YN = #{activateYn}

  					, RESERVED = #{reserved}
  					, MOD_DATE = CURRENT_DATE		
  			WHERE ROOM_NO = #{roomNo}

  			UPDATE ASSET
  			SET
  				  ASSET_NO = #{assetNo}

  					, ASSET_NAME = #{assetName}	


  					, DETAIL = #{detail}


	  				, ACTIVATE_YN = #{activateYn}


  					, RESERVED = #{reserved}

  					, MOD_DATE = CURRENT_DATE	
  			WHERE ASSET_NO = #{assetNo}
