mapper.insert:
INSERT INTO ROOM(
	  ROOM_NO
	, ROOM_NAME
	, ACTIVATE_YN
	, CREATE_DATE
	, DELETE_YN
)VALUE(
	SEQ_MEETING_ROOM.NEXTVAL
	, ${roomName}
	, '
	, ${roomName}
	, ${roomName}
)

mapper.update:
UPDATE ROOM
SET DELETE_YN = 'Y'
WHERE ROOM_NO = ${roomNo}

UPDATE ASSET
SET DELETE_YN = 'Y'
WHERE ASSET_NO = ${assetNo}
